INSERT INTO `cscart_page_descriptions` (`page_id`, `lang_code`, `page`, `description`, `meta_keywords`, `meta_description`, `page_title`, `link`) VALUES
(201, 'es', 'checkout epayco', '<style>\r\n .epayco-title{\r\n max-width: 900px;\r\n display: block;\r\n margin:auto;\r\n color: #444;\r\n font-weight: 700;\r\n margin-bottom: 25px;\r\n }\r\n .loader-container{\r\n position: relative;\r\n padding: 20px;\r\n color: #ff5700;\r\n }\r\n .epayco-subtitle{\r\n font-size: 14px;\r\n }\r\n .epayco-button-render{\r\n transition: all 500ms cubic-bezier(0.000, 0.445, 0.150, 1.025);\r\n transform: scale(1.1);\r\n box-shadow: 0 0 4px rgba(0,0,0,0);\r\n }\r\n .epayco-button-render:hover {\r\n transform: scale(1.2);\r\n }\r\n .animated-points::after{\r\n content: \"\";\r\n animation-duration: 2s;\r\n animation-fill-mode: forwards;\r\n animation-iteration-count: infinite;\r\n animation-name: animatedPoints;\r\n animation-timing-function: linear;\r\n position: absolute;\r\n }\r\n .animated-background {\r\n animation-duration: 2s;\r\n animation-fill-mode: forwards;\r\n animation-iteration-count: infinite;\r\n animation-name: placeHolderShimmer;\r\n animation-timing-function: linear;\r\n color: #f6f7f8;\r\n background: linear-gradient(to right, #7b7b7b 8%, #999 18%, #7b7b7b 33%);\r\n background-size: 800px 104px;\r\n position: relative;\r\n background-clip: text;\r\n -webkit-background-clip: text;\r\n -webkit-text-fill-color: transparent;\r\n }\r\n .loading::before{\r\n -webkit-background-clip: padding-box;\r\n background-clip: padding-box;\r\n box-sizing: border-box;\r\n border-width: 2px;\r\n border-color: currentColor currentColor currentColor transparent;\r\n position: absolute;\r\n margin: auto;\r\n top: 0;\r\n left: 0;\r\n right: 0;\r\n bottom: 0;\r\n content: \" \";\r\n display: inline-block;\r\n background: center center no-repeat;\r\n background-size: cover;\r\n border-radius: 50%;\r\n border-style: solid;\r\n width: 30px;\r\n height: 30px;\r\n opacity: 1;\r\n -webkit-animation: loaderAnimation 1s infinite linear,fadeIn 0.5s ease-in-out;\r\n -moz-animation: loaderAnimation 1s infinite linear, fadeIn 0.5s ease-in-out;\r\n animation: loaderAnimation 1s infinite linear, fadeIn 0.5s ease-in-out;\r\n }\r\n @keyframes animatedPoints{\r\n 33%{\r\n content: \".\"\r\n }\r\n 66%{\r\n content: \"..\"\r\n }\r\n 100%{\r\n content: \"...\"\r\n }\r\n }\r\n @keyframes placeHolderShimmer{\r\n 0%{\r\n background-position: -800px 0\r\n }\r\n 100%{\r\n background-position: 800px 0\r\n }\r\n }\r\n  @keyframes loaderAnimation{\r\n 0%{\r\n -webkit-transform:rotate(0);\r\n transform:rotate(0);\r\n animation-timing-function:cubic-bezier(.55,.055,.675,.19)\r\n }\r\n 50%{\r\n -webkit-transform:rotate(180deg);\r\n transform:rotate(180deg);\r\n animation-timing-function:cubic-bezier(.215,.61,.355,1)\r\n }\r\n 100%{\r\n -webkit-transform:rotate(360deg);\r\n transform:rotate(360deg)\r\n }\r\n }\r\n </style>\r\n<div> \r\n <div class=\"loader-container\">\r\n <div class=\"loading\"></div>\r\n </div>\r\n <p style=\"text-align: center;\" class=\"epayco-title\">\r\n <span class=\"animated-points\">Cargando métodos de pago</span>\r\n <br><small class=\"epayco-subtitle\">Si no se cargan automáticamente, de clic en el botón \"Pagar con ePayco</small>\r\n </p>\r\n  </div>\r\n<p> </p>\r\n<p></p><center><br> <a id=\"btn_epayco\" href=\"#\"><br> <img src=\"https://multimedia.epayco.co/epayco-landing/btns/Boton-epayco-color1.png\"><br> </a><br> </center><br><p></p><p></p>\r\n<script type=\"text/javascript\" src=\"https://epayco-checkout-testing.s3.amazonaws.com/checkout.preprod.js\"></script>\r\n<script src=\"https://cdnjs.cloudflare.com/ajax/libs/jquery/3.1.1/jquery.js\"></script>\r\n<script> \r\n function getQueryParam(param) {\r\n location.search.substr(1)\r\n .split(\"&\")\r\n .some(function(item) {\r\n return item.split(\"=\")[0] == param && (param = item.split(\"=\")[1])\r\n })\r\n return param\r\n }\r\n $(document).ready(function() {\r\n var p_public_key = getQueryParam(\'key\');\r\n if(p_public_key == \'key\'){\r\n   const oldKey = window.location.href.replace(\"?key\", \"&key\");\r\n   let params = new URLSearchParams(oldKey)\r\n   p_public_key = params.get(\'key\')\r\n }\r\n var test = getQueryParam(\'test\'); \r\n var description = getQueryParam(\'description\');\r\n var order_id = getQueryParam(\'order_id\'); \r\n var currency = getQueryParam(\'currency\'); \r\n var total = getQueryParam(\'total\'); \r\n var tax = getQueryParam(\'tax\'); \r\n var sub_total = getQueryParam(\'sub_total\');\r\n var country = getQueryParam(\'country\'); \r\n var external = getQueryParam(\'external\'); \r\n var url = window.location.origin+window.location.pathname+\"?dispatch=\"\r\n const newUrl = url.replace(\"checkout-epayco/\", \"index.php\");\r\n var lang = getQueryParam(\'lang\'); \r\n var handler = ePayco.checkout.configure({\r\n key: p_public_key,\r\n test: test\r\n });\r\n var data={\r\n name: \"Order #\"+order_id,\r\n description: \"Order #\"+order_id,\r\n invoice: order_id,\r\n currency: currency,\r\n amount: total,\r\n tax_base: sub_total,\r\n tax: tax,\r\n country: country,\r\n lang: lang,\r\n external: external,\r\n extra1: order_id,\r\n confirmation: newUrl+\"payment_notification.confirmation&payment=epayco&order_id=\"+order_id,\r\n response: newUrl+\"payment_notification.response&payment=epayco&order_id=\"+order_id,\r\n }\r\n var openChekout = function () {\r\n handler.open(data);\r\n }\r\n  handler.open(data);\r\n var bntPagar = document.getElementById(\"btn_epayco\");\r\n bntPagar.addEventListener(\"click\", openChekout);\r\n  })</script>', 'checkout epayco', 'checkout epayco', 'checkout epayco', '');